#include <vector> 
#include <string> 
#include <cstdio> 
#include <cmath> 
#include <cstring> 
#include <cstdlib> 
#include <iostream> 
#include <sstream> 
#include <algorithm> 
#include <utility> 
#include <queue> 
#include <map> 
 
using namespace std; 
 
class TheXGame{ 
public: 
  int min(int a,int b){ 
    return a<b?a:b; 
  } 
 
  int n,m,a[100],k,r,s; 
  int firstMove(string board){ 
    n=board.size(); 
    m=0; 
    int i,j; 
    for(i=0;i<n;i++){ 
      if (board[i]=='-') m++; 
    } 
    if (m<=2) return -1; 
    m=0; 
    for(i=0;i<n;){ 
      if (board[i]=='-'){ 
        for(j=i+1;j<n;j++) if (board[j]!='-') break; 
        a[m++]=j-i; 
        i=j; 
      }else{ 
        i++; 
      } 
    } 
    k=0; 
    int best=100; 
    for(i=0;i<m;i++) k^=a[i]; 
    for(i=0;i<m;i++){ 
      j=k^a[i]; 
      for(r=0;r<a[i];r++){ 
        for(s=0;s+r<a[i];s++){ 
          if (!(j^s^r)){ 
            best=min(best,a[i]-s-r); 
          } 
        } 
      } 
    } 
    if (best==100) best=-1; 
    return best; 
  } 
   
 
}; 
 
 
 
