using namespace std;
 
class Stick {
public:
  int pieces(int);
};
 
int Stick::pieces(int x) {
  int i=1,j,cnt=0;
  for(j=0;j<=6;j++){
    if((x&i)){
    cnt++;
    }
    i*=2;
  }
  return cnt;
}
 
 
