using namespace std;
 
 
class BallAndHats {
public:
  int getHat(string h, int n) {
    int pos = -1;
    for ( int i = 0; i < h.size(); i++ ) {
      if ( h[i] == 'o' ) pos = i;
    }
    
    if ( n == 0 ) return pos;
    
    if ( pos == 1 ) {
      if ( n%2 ) return 0;
      else      return 1;
    }
    else {
      if ( n%2 ) return 1;
      else      return 0;
    }
  }
};
 
 
 
